import { useEffect, useState } from "react";
import axios from "axios";
import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const ViewProduct = () => {
    const [images, setImages] = useState([]);
    const [filteredImages, setFilteredImages] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [cart, setCart] = useState([]);

    // Example categories
    const categories = ["All", "Dry Products", "Seafood", "Handmade Eatable", "Beverages", "Dairy Products", "Snacks"];

    // Fetch products from the API
    useEffect(() => {
        axios.get('http://localhost:5000/api/products/getImages')
            .then(res => {
                setImages(res.data);
                setFilteredImages(res.data);
            })
            .catch(err => console.log(err));
    }, []);

    // Handle category selection
    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
    };

    // Handle Add to Cart
    const handleAddToCart = (product) => {
        setCart(prevCart => [...prevCart, product]);
        alert(`${product.name} added to cart!`);
    };

    // Filter images when category or search query changes
    useEffect(() => {
        let filtered = images;

        // Filter by category
        if (selectedCategory !== "All") {
            filtered = filtered.filter(image => image.category === selectedCategory);
        }

        // Filter by search query
        if (searchQuery !== "") {
            filtered = filtered.filter(image =>
                image.name.toLowerCase().includes(searchQuery.toLowerCase())
            );
        }

        setFilteredImages(filtered);
    }, [selectedCategory, searchQuery, images]);

    return (
        <div>
            <Header />

            {/* Category Navigation */}
            <div className="flex justify-center bg-white p-4 border-b border-gray-200">
                {categories.map(category => (
                    <button
                        key={category}
                        className={`px-4 py-2 mx-2 text-sm font-medium border-b-2 ${selectedCategory === category ? 'border-green-600' : 'border-transparent'} hover:border-green-600`}
                        onClick={() => handleCategoryChange(category)}
                    >
                        {category}
                    </button>
                ))}
            </div>

            {/* Search Bar */}
            <div className="flex justify-center p-4 bg-white">
                <input
                    type="text"
                    placeholder="Search products..."
                    className="p-2 border rounded-md w-1/2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
            </div>

            {/* Product Grid */}
            <div className="w-full grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-4 gap-6 p-6 bg-gray-100">
                {filteredImages.map(image => (
                    <div 
                        key={image._id} 
                        className="bg-white rounded-lg shadow-lg overflow-hidden transition-transform transform hover:scale-105 border border-green-600"
                    >
                        <div className="w-full h-64">
                            <img src={`http://localhost:5000/Images/${image.image}`} alt={image.name} className="w-full h-full object-cover"/>
                        </div>
                        <div className="p-4">
                            <p className="text-lg font-semibold text-green-600">{image.name}</p>
                            <p className="text-gray-700 mt-2">
                                {image.discount ? (
                                    <>
                                        <span className="line-through text-gray-500">${image.price}</span>
                                        <span className="ml-2 font-bold text-green-600">
                                            ${((1 - image.discount / 100) * image.price).toFixed(2)}
                                        </span>
                                    </>
                                ) : (
                                    <span className="text-green-600">${image.price}</span>
                                )}
                            </p>
                            <button
                                className="mt-4 py-2 px-4 w-full rounded border border-green-600 text-green-600 hover:bg-green-100"
                                onClick={() => handleAddToCart(image)}
                            >
                                Add to Cart
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            <Footer />
        </div>
    );
};

export default ViewProduct;
