import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const AddProduct = () => {
    const [file, setFile] = useState(null);
    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [discount, setDiscount] = useState('');
    const [imagePreview, setImagePreview] = useState(null);
    const [category, setCategory] = useState('Dry Products');
    const [description, setDescription] = useState('');
    const [expDate, setExpDate] = useState('');
    const [manufactureDate, setManufactureDate] = useState('');

    // Validation state
    const [errors, setErrors] = useState({});

    const navigate = useNavigate();

    // Validate form fields on change
    useEffect(() => {
        validateField('name', name);
        validateField('quantity', quantity);
        validateField('price', price);
        validateField('description', description);
        validateField('discount', discount);
        validateField('manufactureDate', manufactureDate);
        validateField('expDate', expDate);
    }, [name, quantity, price, description, discount, manufactureDate, expDate]);

    // Validate each field in real-time
    const validateField = (field, value) => {
        let errorsCopy = { ...errors };
        const today = new Date().toISOString().split('T')[0];

        switch (field) {
            case 'name':
                if (!value.trim()) errorsCopy.name = "Product name is required";
                else if (/\d/.test(value)) errorsCopy.name = "Product name cannot contain numbers";
                else delete errorsCopy.name;
                break;
            case 'quantity':
                if (!value || value <= 0) errorsCopy.quantity = "Quantity must be a positive number";
                else delete errorsCopy.quantity;
                break;
            case 'price':
                if (!value || value <= 0) errorsCopy.price = "Price must be a positive number";
                else delete errorsCopy.price;
                break;
            case 'description':
                if (!value.trim()) errorsCopy.description = "Product description is required";
                else delete errorsCopy.description;
                break;
            case 'discount':
                if (value && (isNaN(value) || value < 0 || value > 100)) errorsCopy.discount = "Discount must be a number between 0 and 100";
                else delete errorsCopy.discount;
                break;
            case 'manufactureDate':
                if (!value) errorsCopy.manufactureDate = "Manufacture date is required";
                else if (value > today) errorsCopy.manufactureDate = "Manufacture date cannot be in the future";
                else delete errorsCopy.manufactureDate;
                break;
            case 'expDate':
                if (!value) errorsCopy.expDate = "Expiration date is required";
                else if (value < today) errorsCopy.expDate = "Expiration date cannot be in the past";
                else if (value < manufactureDate) errorsCopy.expDate = "Expiration date cannot be before manufacture date";
                else delete errorsCopy.expDate;
                break;
            default:
                break;
        }
        setErrors(errorsCopy);
    };

    const handleUpload = () => {
        if (Object.keys(errors).length > 0) return; // Don't submit if there are validation errors

        const formData = new FormData();
        formData.append('file', file);
        formData.append('name', name);
        formData.append('description', description);
        formData.append('quantity', quantity);
        formData.append('price', price);
        formData.append('discount', discount);
        formData.append('category', category);
        formData.append('expDate', expDate);
        formData.append('manufactureDate', manufactureDate);

        axios.post('http://localhost:5000/api/products/upload', formData)
            .then(res => {
                console.log('Upload response:', res.data);
                navigate('/admin-product');
                window.location.reload();
            })
            .catch(err => console.log(err));
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);

        const reader = new FileReader();
        reader.onloadend = () => {
            setImagePreview(reader.result);
        };
        reader.readAsDataURL(selectedFile);
    };

    const Header = () => {
        return (
            <div className="bg-green-800 text-white p-4 flex justify-center items-center shadow-md">
                <div className="flex space-x-6">
                    <Link to='/view-product' className="hover:text-green-300 transition-colors duration-300">Home</Link>
                    <Link to='/add-product' className="hover:text-green-300 transition-colors duration-300">Add Product</Link>
                    <Link to='/admin-product' className="hover:text-green-300 transition-colors duration-300">Manage Product</Link>
                    <Link to='/dashboard' className="hover:text-green-300 transition-colors duration-300">Dashboard</Link>
                </div>
            </div>
        );
    };

    return (
        <div>
            <Header />
            <div className="bg-green-50 min-h-screen">
                <div className="max-w-4xl mx-auto p-8 bg-white shadow-md rounded-lg border border-green-200">
                    <h2 className="text-3xl font-bold mb-6 text-center text-green-800">Add New Product</h2>

                    <div className="space-y-8">
                        <div className="space-y-6">
                            <h3 className="text-xl font-semibold mb-4 text-green-700">General Information</h3>
                            <input 
                                type="text" 
                                placeholder="Product Name" 
                                value={name} 
                                onChange={e => setName(e.target.value)} 
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            />
                            {errors.name && <p className="text-red-600">{errors.name}</p>}

                            <textarea
                                placeholder="Product Description"
                                value={description}
                                onChange={e => setDescription(e.target.value)}
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            ></textarea>
                            {errors.description && <p className="text-red-600">{errors.description}</p>}

                            <select
                                value={category}
                                onChange={e => setCategory(e.target.value)}
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            >
                                <option value="Dry Products">Dry Products</option>
                                <option value="Seafood">Seafood</option>
                                <option value="Handmade Eatable">Handmade Eatable</option>
                                <option value="Beverages">Beverages</option>
                                <option value="Dairy Products">Dairy Products</option>
                                <option value="Snacks">Snacks</option>
                            </select>
                        </div>

                        <div className="space-y-6">
                            <h3 className="text-xl font-semibold mb-4 text-green-700">Pricing and Stock</h3>
                            <input 
                                type="text" 
                                placeholder="Base Price" 
                                value={price} 
                                onChange={e => setPrice(e.target.value)} 
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            />
                            {errors.price && <p className="text-red-600">{errors.price}</p>}

                            <input 
                                type="number" 
                                placeholder="Stock" 
                                value={quantity} 
                                onChange={e => setQuantity(e.target.value)} 
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            />
                            {errors.quantity && <p className="text-red-600">{errors.quantity}</p>}
                        </div>

                        <div className="space-y-6">
                            <h3 className="text-xl font-semibold mb-4 text-green-700">Manufacture and Expiration Dates</h3>
                            <div className="space-y-4">
                                <div>
                                    <h4 className="text-lg font-semibold text-green-600">Manufacture Date</h4>
                                    <input
                                        type="date"
                                        value={manufactureDate}
                                        onChange={e => setManufactureDate(e.target.value)}
                                        className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                                    />
                                    {errors.manufactureDate && <p className="text-red-600">{errors.manufactureDate}</p>}
                                </div>

                                <div>
                                    <h4 className="text-lg font-semibold text-green-600">Expiration Date</h4>
                                    <input
                                        type="date"
                                        value={expDate}
                                        onChange={e => setExpDate(e.target.value)}
                                        className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                                    />
                                    {errors.expDate && <p className="text-red-600">{errors.expDate}</p>}
                                </div>
                            </div>
                        </div>

                        <div className="space-y-6">
                            <h3 className="text-xl font-semibold mb-4 text-green-700">Discount</h3>
                            <input
                                type="number"
                                placeholder="Discount Percentage"
                                value={discount}
                                onChange={e => setDiscount(e.target.value)}
                                className="w-full px-4 py-2 border border-green-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                            />
                            {errors.discount && <p className="text-red-600">{errors.discount}</p>}
                        </div>

                        <div className="space-y-6">
                            <h3 className="text-xl font-semibold mb-4 text-green-700">Upload Image</h3>
                            <input type="file" onChange={handleFileChange} />
                            {imagePreview && <img src={imagePreview} alt="Preview" className="mt-4" />}
                        </div>

                        <button
                            onClick={handleUpload}
                            className="w-full py-3 px-6 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors duration-300"
                        >
                            Add Product
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddProduct;
