import { useEffect, useState } from "react";
import axios from "axios";

const ViewProduct = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("All");

    // Fetch products from the API
    useEffect(() => {
        axios.get('http://localhost:5000/api/products/getImages')
            .then(res => {
                setProducts(res.data);
                setFilteredProducts(res.data);
            })
            .catch(err => console.log(err));
    }, []);

    // Filter products based on search query and category
    useEffect(() => {
        let filtered = products;

        if (selectedCategory !== "All") {
            filtered = filtered.filter(product => product.category === selectedCategory);
        }

        if (searchQuery) {
            filtered = filtered.filter(product =>
                product.name.toLowerCase().includes(searchQuery.toLowerCase())
            );
        }

        setFilteredProducts(filtered);
    }, [selectedCategory, searchQuery, products]);

    return (
        <div className="p-6">
            {/* Category Filter */}
            <div className="flex justify-center mb-6">
                <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="border border-green-500 px-4 py-2 rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                >
                    <option value="All">All Categories</option>
                    <option value="Dry Products">Dry Products</option>
                    <option value="Seafood">Seafood</option>
                    <option value="Dairy Products">Dairy Products</option>
                    <option value="Beverages">Beverages</option>
                    <option value="Snacks">Snacks</option>
                </select>
            </div>

            {/* Search Bar */}
            <div className="flex justify-center mb-8">
                <input
                    type="text"
                    placeholder="Search products..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full md:w-1/2 border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                />
            </div>

            {/* Product Grid */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {filteredProducts.map((product) => (
                    <div key={product._id} className="border border-gray-200 rounded-lg shadow-lg p-4 bg-white">
                        {/* Product Image */}
                        <div className="h-40 flex justify-center items-center">
                            <img
                                src={`http://localhost:5000/Images/${product.image}`}
                                alt={product.name}
                                className="object-contain h-full"
                            />
                        </div>

                        {/* Product Name */}
                        <h3 className="text-gray-800 text-lg font-semibold mt-4">
                            {product.name}
                        </h3>

                        {/* Product Category */}
                        <p className="text-gray-500 text-sm">
                            {product.category}
                        </p>

                        {/* Price Section */}
                        <div className="mt-2 text-lg text-green-600 font-bold">
                            <span>â‚¬{product.price.toFixed(2)}</span>
                        </div>

                        {/* Add to Cart */}
                        <div className="flex items-center mt-4">
                            <input
                                type="number"
                                min="1"
                                defaultValue="1"
                                className="border w-12 p-2 rounded text-center"
                            />
                            <button className="ml-2 flex-1 bg-green-600 text-white px-4 py-2 rounded-md">
                                Add
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewProduct;
