import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
    const [categoriesData, setCategoriesData] = useState([]);
    const [products, setProducts] = useState([]);

    // Fetch products data
    useEffect(() => {
        axios.get('http://localhost:5000/api/products/getImages')
            .then(res => {
                setProducts(res.data);
                const categories = {};
                res.data.forEach(product => {
                    if (categories[product.name]) {
                        categories[product.name] += 1;
                    } else {
                        categories[product.name] = 1;
                    }
                });
                setCategoriesData(categories);
            })
            .catch(err => console.log(err));
    }, []);

    // Prepare data for charts
    const pieData = {
        labels: Object.keys(categoriesData),
        datasets: [
            {
                data: Object.values(categoriesData),
                backgroundColor: [
                    '#4CAF50', // Green
                    '#81C784', // Light Green
                    '#A5D6A7', // Lighter Green
                    '#66BB6A', // Medium Green
                    '#388E3C', // Dark Green
                ],
            },
        ],
    };

    const barData = {
        labels: Object.keys(categoriesData),
        datasets: [
            {
                label: 'Number of Products',
                data: Object.values(categoriesData),
                backgroundColor: '#4CAF50', // Green
            },
        ],
    };

    // Chart options to control the size
    const chartOptions = {
        responsive: true,
        plugins: {
            legend: {
                display: true,
                position: 'top',
            },
            tooltip: {
                callbacks: {
                    label: function(tooltipItem) {
                        return tooltipItem.label + ': ' + tooltipItem.raw;
                    }
                }
            }
        },
        layout: {
            padding: 20,
        },
        maintainAspectRatio: false,
    };

    return (
        <div className="p-6">
            <h2 className="text-2xl font-semibold mb-6 text-green-700">Dashboard</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Pie Chart */}
                <div className="bg-white p-4 rounded-lg shadow-lg h-80 border border-green-300">
                    <h3 className="text-lg font-semibold mb-4 text-green-700">Product Categories Distribution</h3>
                    <Pie data={pieData} options={chartOptions} height={300} />
                </div>

                {/* Bar Chart */}
                <div className="bg-white p-4 rounded-lg shadow-lg h-80 border border-green-300">
                    <h3 className="text-lg font-semibold mb-4 text-green-700">Number of Products by Category</h3>
                    <Bar data={barData} options={chartOptions} height={300} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
