import React, { useState } from 'react';
import axios from 'axios';

const UpdateProductPopup = ({ product, onClose, onUpdate }) => {
    const [name, setName] = useState(product.name);
    const [description, setDescription] = useState(product.description);
    const [quantity, setQuantity] = useState(product.quantity);
    const [price, setPrice] = useState(product.price);
    const [discount, setDiscount] = useState(product.discount || 0);
    const [category, setCategory] = useState(product.category || '');
    const [expDate, setExpDate] = useState(product.expDate ? new Date(product.expDate).toISOString().substr(0, 10) : '');
    const [manufactureDate, setManufactureDate] = useState(product.manufactureDate ? new Date(product.manufactureDate).toISOString().substr(0, 10) : '');

    const [errors, setErrors] = useState({});

    const handleSave = () => {
        const today = new Date().toISOString().split('T')[0];
        let errors = {};

        if (!name.trim()) errors.name = "Product name is required";
        if (!description.trim()) errors.description = "Product description is required";
        if (!quantity || quantity <= 0) errors.quantity = "Quantity must be a positive number";
        if (!price || price <= 0) errors.price = "Price must be a positive number";
        if (isNaN(discount) || discount < 0 || discount > 100) errors.discount = "Discount must be a number between 0 and 100";
        if (expDate < today) errors.expDate = "Expiration date cannot be before the current date";
        if (expDate < manufactureDate) errors.expDate = "Expiration date cannot be before the manufacture date";
        if (manufactureDate > today) errors.manufactureDate = "Manufacture date cannot be after the current date";

        setErrors(errors);

        if (Object.keys(errors).length > 0) return;

        axios.put(`http://localhost:5000/api/products/update/${product._id}`, {
            name, description, quantity, price, discount, category, expDate, manufactureDate
        })
        .then(() => {
            onUpdate();
            onClose();
        })
        .catch(err => console.log(err));
    };

    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full relative">
                <button 
                    onClick={onClose} 
                    className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
                >
                    &times;
                </button>
                <h3 className="text-xl font-semibold mb-4 text-green-600">Update Product</h3>
                
                {/* Form Fields */}
                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Product Name</label>
                    <input
                        type="text"
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                        placeholder="Product Name"
                    />
                    {errors.name && <p className="text-red-600 text-sm mt-1">{errors.name}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Description</label>
                    <input
                        type="text"
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                        placeholder="Description"
                    />
                    {errors.description && <p className="text-red-600 text-sm mt-1">{errors.description}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Price</label>
                    <input
                        type="text"
                        value={price}
                        onChange={e => setPrice(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                        placeholder="Price"
                    />
                    {errors.price && <p className="text-red-600 text-sm mt-1">{errors.price}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Quantity</label>
                    <input
                        type="number"
                        value={quantity}
                        onChange={e => setQuantity(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                        placeholder="Quantity"
                    />
                    {errors.quantity && <p className="text-red-600 text-sm mt-1">{errors.quantity}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Discount (%)</label>
                    <input
                        type="number"
                        value={discount}
                        onChange={e => setDiscount(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                        placeholder="Discount (%)"
                    />
                    {errors.discount && <p className="text-red-600 text-sm mt-1">{errors.discount}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Category</label>
                    <select
                        value={category}
                        onChange={e => setCategory(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                    >
                        <option value="">Select Category</option>
                        <option value="Dry Products">Dry Products</option>
                        <option value="Seafood">Seafood</option>
                        <option value="Handmade Eatable">Handmade Eatable</option>
                        <option value="Beverages">Beverages</option>
                        <option value="Dairy Products">Dairy Products</option>
                        <option value="Snacks">Snacks</option>
                    </select>
                    {errors.category && <p className="text-red-600 text-sm mt-1">{errors.category}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Expiration Date</label>
                    <input
                        type="date"
                        value={expDate}
                        onChange={e => setExpDate(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                    />
                    {errors.expDate && <p className="text-red-600 text-sm mt-1">{errors.expDate}</p>}
                </div>

                <div className="mb-4">
                    <label className="block text-gray-700 mb-2">Manufacture Date</label>
                    <input
                        type="date"
                        value={manufactureDate}
                        onChange={e => setManufactureDate(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                    />
                    {errors.manufactureDate && <p className="text-red-600 text-sm mt-1">{errors.manufactureDate}</p>}
                </div>

                <div className="flex justify-between">
                    <button 
                        onClick={handleSave}
                        className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                    >
                        Save
                    </button>
                    <button 
                        onClick={onClose}
                        className="bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700"
                    >
                        Cancel
                    </button>
                </div>
            </div>
        </div>
    );
};

export default UpdateProductPopup;
